con:config.script:
import com.eviware.soapui.support.XmlHolder
//i am creating a random email id
Random rnd = new Random()
def emailrandom="rajdeep"+rnd.next(200)+"@gmail.com"
log.info emailrandom
def tc = testRunner.testCase.testSuite.project.testSuites["Create Customer Profile"].testCases["Add Customer"]
tc.testSteps['NewCustomer'].setPropertyValue("email",emailrandom)
def runner=tc.run(null,false)

def tstepCreateCustomer =tc.testSteps['CreateCustomer'].getPropertyValue('response')
def holderCreateCustomer= new XmlHolder(tstepCreateCustomer)
def idvalueCreateCustomer = holderCreateCustomer.getNodeValue("//*:id")
log.info "The Customer Id Is:$idvalueCreateCustomer"

def tstepCById =tc.testSteps['Get Customer By Id'].getPropertyValue('response')
def holderCById= new XmlHolder(tstepCById)
def idvalueCustomerById = holderCById.getNodeValue("//*:id")

assert idvalueCreateCustomer==idvalueCustomerById

def tstepAllCustomer =tc.testSteps['Get All Customer'].getPropertyValue('response')
def holderAllCustomer= new XmlHolder(tstepAllCustomer)
//the service provides response of list of customers and i am trying to count the list of customers
def nodeCount=holderAllCustomer.getNodeValue("count(//result/items/item/id)")
log.info "The Total Customers Added Are"+ nodeCount

def tstepDeleteCustomer =tc.testSteps['Delete Customer'].getPropertyValue('response')
def holderDeleteCustomer= new XmlHolder(tstepDeleteCustomer)
def idvalueDeleteCustomer = holderDeleteCustomer.getNodeValue("//*:result")
log.info "The Customer with Id $idvalueCreateCustomer Has Been Deleted :$idvalueDeleteCustomer"

def tstepCheckCustomerById =tc.testSteps['Check Customer By Id'].getPropertyValue('response')
def holderCheckCustomerById= new XmlHolder(tstepCheckCustomerById)
def idvalueCheckCustomerById = holderCheckCustomerById.getNodeValue("//*:Text")
log.info "idvalueCheckCustomerById" +idvalueCheckCustomerById
